mapper.update:
update t_student
set name = #{name,jdbcType=VARCHAR},
  sex = #{sex,jdbcType=TINYINT},
  age = #{age,jdbcType=INTEGER},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, sex, age, create_time
from t_student
where id = #{id,jdbcType=INTEGER}
